// –ø—Ä–∏–º–µ—Ä —Ñ–∞–π–ª–∞ –ø–ª–∞–≥–∏–Ω–∞ ---------------
(function($) {
    $.fn.duplicate = function(settings) {
        let defaults = {
            delim: ' ',  // –∑–Ω–∞—á–µ–Ω–∏–µ —Ä–∞–∑–¥–µ–ª–∏—Ç–µ–ª—è –ø–æ —É–º–æ–ª—á, –µ—Å–ª–∏ –≤ settings –Ω–µ—Ç –∫–ª—é—á–∞ delim
            count: 2     // –∑–Ω–∞—á–µ–Ω–∏–µ –∫–æ–ª–∏—á –ø–æ–≤—Ç–æ—Ä  –ø–æ —É–º–æ–ª—á, –µ—Å–ª–∏ –≤ settings –Ω–µ—Ç –∫–ª—é—á–∞ count
        };

        let options = $.extend(defaults, settings);  // –æ–±—ä–µ–¥–∏–Ω–∏—Ç—å –¥–≤–∞ –æ–±—ä–µ–∫—Ç–∞, –ø–µ—Ä–≤—ã–π –æ–±—ä–µ–∫—Ç –º–æ–¥–∏—Ñ–∏—Ü–∏—Ä—É–µ—Ç—Å—è –≤—Ç–æ—Ä—ã–º

        // console.log(this);  // this –∑–¥–µ—Å—å –æ–±—ä–µ–∫—Ç jquery, –∞ –Ω–µ —ç–ª–µ–º–µ–Ω—Ç DOM

        this.each(function() {
            let elem = $(this);  // elem - —ç—Ç–æ —É–∂–µ —Å—Å—ã–ª–∫–∞ –Ω–∞ —Å–∞–º —ç–ª–µ–º–µ–Ω—Ç DOM

            let text = elem.html();
            let result = '';

            for(let i = 0; i < options.count; i++) {
                result += text;

                if(i < options.count - 1) {
                    result += options.delim;
                }
            }

            elem.html(result);
        });

        return this;
    };
})(jQuery); 

// –ø—Ä–∏–º–µ—Ä –≤—ã–∑–æ–≤–∞:  $('.ask').duplicate( {delim: ' - ', count: 3} );


